inpt = """"""

solutions = Set{ASCIIString}()
push!(solutions, inpt) # to cover the case of no matches. Will be subtracted at the end
subst = r"(\w+) => (\w+)"

#part 1
for line in replacements
	m = match(subst, line)
	for i in 1:length(inpt)
		sol = inpt[1:i-1]*replace(inpt[i:end], m.captures[1], m.captures[2], 1)
		push!(solutions, sol)
	end
end
println(length(solutions)-1) # one is the original molecule, in case of no substitutions


